    private void btnPromEstActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnPromEstActionPerformed
        int n1,n2,n3,nMa;
        double nE,pE;
        n1=Integer.parseInt(txtNo1.getText());
        n2=Integer.parseInt(txtNo2.getText());
        n3=Integer.parseInt(txtNo3.getText());
        nMa=Integer.parseInt(txtNm.getText()); 
        nE=(n1+n2+n3)/3.0;
        nEs+=nE;
        pE=nEs/nMa;
        txtA.setText(pE+"\n");
    }//GEN-LAST:event_btnPromEstActionPerformed

