    private void btnCalcularActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnCalcularActionPerformed
        double radio,volumen, superficie;
        radio=Integer.parseInt(txtRadio.getText());
        Esfera Esfera1=new Esfera(radio);
        volumen=Esfera1.volumen();
        superficie=Esfera1.superficie();
        txtVolA.setText(String.valueOf(volumen));
        txtSupA.setText(String.valueOf(superficie));
        Esfera Esfera2=new Esfera(radio,2);
        volumen=Esfera2.volumen();
        superficie=Esfera2.superficie();
        txtVolB.setText(String.valueOf(volumen));
        txtSupB.setText(String.valueOf(superficie));
    }//GEN-LAST:event_btnCalcularActionPerformed

