    private void btMostrarActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btMostrarActionPerformed
        String tex1=txtEmpresa.getText();
        String tex2=txtNombre.getText();
        String tex3=txtEdad.getText();
        String tex4=txtEstatura.getText();
        String datos;
        if(tex1.compareTo("")!=0&&tex2.compareTo("")!=0&&tex3.compareTo("")!=0&&tex4.compareTo("")!=0){
            String Empresa=tex1;
            String Nombre=tex2;
            int edad=Integer.parseInt(tex3);
            double Estatura=Double.parseDouble(tex4);
            Persona b=new Persona(Empresa,Nombre,edad,Estatura);
            datos=b.Mostrar();
            txtA.setText(null);
            txtA.append(datos);
        }else{
            Persona a=new Persona();
            datos=a.Mostrar();
            txtA.setText(null);
            txtA.append(datos);
        }
    }//GEN-LAST:event_btMostrarActionPerformed

