    private void btnProcesarActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnProcesarActionPerformed
        double num1 = Integer.parseInt(txtNum1.getText());
        double num2 = Integer.parseInt(txtNum2.getText());
        double num3 = Integer.parseInt(txtNum3.getText());
        double num4 = Integer.parseInt(txtNum4.getText());
        double num5 = Integer.parseInt(txtNum5.getText());
        double prom, ma, me;
              
        ma = Math.max(num1,num2);
        ma = Math.max(ma,num3);
        ma = Math.max(ma,num4);
        ma = Math.max(ma,num5);
        
        me=Math.min(num1,Math.min(num2,Math.min(num3,Math.min(num4, num5))));
        
        prom= (num1+num2+num3+num4+num5-ma-me)/3;
        
        txtArea.setText("El Promedio es: " +prom);
        
    }//GEN-LAST:event_btnProcesarActionPerformed

