    private void btnProcesarActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnProcesarActionPerformed
        int qRopa;
        double precio, iCom, iD, iP;
        
        qRopa = Integer.parseInt(txtCant.getText());
        precio = Double.parseDouble(txtPrecio.getText());
        
        iCom = qRopa * precio;
        iD = (iCom * 5)/100;
        iP = iCom - iD;
        
        txtArea.setText("El importe de compra es: " + iCom +"\n");
        txtArea.append("El importe de descuento es: " + iD + "\n");
        txtArea.append("El importe a pagar es: " + iP);
        
    }//GEN-LAST:event_btnProcesarActionPerformed

