    private void btnProcesarActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnProcesarActionPerformed
        int monto = Integer.parseInt(txtMonto.getText());
        double tM, tT, tN;
        
        if(monto<200){
            tM = (monto*35)/100;
            tT = (monto*35)/100;
            tN = monto - tT - tM;
        }else if(monto<500){
            tM = (monto*70)/100;
            tT = 0;
            tN = monto - tT - tM;
        }else{
            tM = (monto*40)/100;
            tT = (monto*35)/100;
            tN = monto - tT - tM;
        }
        txtArea.setText("Al turno maÃ±ana le toca: " + tM + "\n");
        txtArea.append("Al turno tarde le toca: " + tT + "\n");
        txtArea.append("El turno noche le toca: " + tN);
    }//GEN-LAST:event_btnProcesarActionPerformed

