    private void btnProcesarActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnProcesarActionPerformed
        double precio, iC, iP, iD=0;
        int tipo, qHamb;
        
        qHamb = Integer.parseInt(txtCantidad.getText());
        tipo = cmbTipo.getSelectedIndex();
        
        switch(tipo){
            case 0:
               iC = qHamb *4.5;
               break;
            case 1: 
                iC = qHamb *5.0;
                break;
            case 2: 
                iC = qHamb *5.0;
                break;
            default:
                iC = qHamb * 13.5;
        }
        if(iC>20){
            iD = (iC*10)/100;
        }
        iP = iC - iD;
        
        txtArea.setText("El importe de compra es: " + iC + "\n");
        txtArea.append("El importe de descuento es : " + iD + "\n");
        txtArea.append("El importe a pagar es: " + iP);
    }//GEN-LAST:event_btnProcesarActionPerformed

