    private void btnProcesarActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnProcesarActionPerformed
        int nDias, tipo, gen, edad;
        double iC, iP, iD=0;
        
        nDias = Integer.parseInt(txtDias.getText());
        edad = Integer.parseInt(txtEdad.getText());
        tipo = cmbTipo.getSelectedIndex();
        gen = cmbGenero.getSelectedIndex();
        
        switch(tipo){
            case 0: 
                iC = nDias * 20.5;
                break;
            case 1:
                iC = nDias * 35.9;
                break;
            case 2:
                iC = nDias * 28.0;
                break;
            case 3: 
                iC = nDias * 18.5;
                break;
            default:
                iC = nDias * 25.0;
        }
        if(edad>52){
           switch(gen){
            case 0: 
                iD = (iC * 13)/100;
                break;
        } 
        }
        iP = iC + iD;
        txtArea.setText("El monto a pagar es: "+ iP);
    }//GEN-LAST:event_btnProcesarActionPerformed

