    private void btnProcesarActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnProcesarActionPerformed
        double alt, base, area;
        String mensaje;
        
        alt = Double.parseDouble(txtAltura.getText());
        base = Double.parseDouble(txtBase.getText());
        
        area = (alt * base)/2;
        
        if(area<100){
            mensaje = "Rectangulo pequeÃ±o";
        }else if(area<1000){
            mensaje = "Rectangulo mediano";
        }else{
            mensaje = "Rectangulo grande";
        }
        txtArea.setText(mensaje+"\n");
        txtArea.append("El area del rectangulo es: " + area);
    }//GEN-LAST:event_btnProcesarActionPerformed

