    private void btnProcesarActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnProcesarActionPerformed
        int cant, tCua,calidad;
        double iC, iD, iP;
        
        tCua = cmbTipo.getSelectedIndex();
        calidad = cmbCalidad.getSelectedIndex();
        cant = Integer.parseInt(txtCant.getText());
        
        if(calidad == 0){
           switch(tCua){
            case 0:
                iC = 5.0 * cant;
                break;
            case 1:
                iC = 6.0 * cant;
                break;
            case 2:
                iC = 7.0 * cant;
                break;
            default:
                iC = 13.0 * cant;
           }
        }else{
            switch(tCua){
            case 0:
                iC = 6.5 * cant;
                break;
            case 1:
                iC = 7.5 * cant;
                break;
            case 2:
                iC = 8.0 * cant;
                break;
            default:
                iC = 15.50* cant;
           }
        }
        
        
    }//GEN-LAST:event_btnProcesarActionPerformed

