    private void btnProcesarActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnProcesarActionPerformed
        nombre = txtNombre.getText();
        pagoHora = Double.parseDouble(txtPago.getText());
        numHoras = Integer.parseInt(txtHoras.getText());
        String clave;
        double sueldoBruto, sueldoNeto;
        
        clave = generarClave();
        sueldoBruto = calcularSueldoBruto();
        sueldoNeto = calcularSueldoNeto(sueldoBruto);
        
        mostrarReporte(sueldoBruto, sueldoNeto, clave);
    }//GEN-LAST:event_btnProcesarActionPerformed
    public String generarClave(){
        String subNombre = nombre.substring(0,2);
        String clave;
        int ale;
        
        ale = (int)(Math.random()*(999-100+1)+100);
        
        clave = subNombre + ale;
        return clave;
    }
    public double calcularSueldoBruto(){
        double sueldoBruto;
        
        sueldoBruto = numHoras * pagoHora;
        return sueldoBruto;
    }
    public double calcularSueldoNeto(double sueldoBruto){
        double impuesto, sueldoNeto;
        
        if(sueldoBruto<=3000){
            impuesto = (sueldoBruto*11.5)/100;
        }else if(sueldoBruto<=5000){
            impuesto = (sueldoBruto*12.5)/100;
        }else{
            impuesto = (sueldoBruto*13)/100;
        }
        sueldoNeto = sueldoBruto - impuesto;
        return sueldoNeto;
    }
    private void mostrarReporte(double sueldoBruto, double sueldoNeto, String clave){
        txtArea.setText("La clave del trabajador es: " + clave + "\n");
        txtArea.append("El sueldo Bruto es: " + sueldoBruto + "\n");
        txtArea.append("El sueldo neto es: " + sueldoNeto);
    }
