    double tarifa;
    int horas;
    double sueldo, descuento = 0;
    private void btnProcesarActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnProcesarActionPerformed
        tarifa = Double.parseDouble(txtTarifa.getText());
        horas = Integer.parseInt(txtHoras.getText());
        
        calcularSueldo();
        sueldoBruto();
    }//GEN-LAST:event_btnProcesarActionPerformed
    private void calcularSueldo(){
        sueldo = tarifa * horas;
        if(sueldo>=4000){
            descuento = (sueldo*10)/100;
        }
        sueldo = sueldo - descuento;
        txtArea.setText("El sueldo es = " +sueldo + "\n");
    }
    private void sueldoBruto(){
        double sueldoBruto;
        calcularSueldo();
        sueldoBruto = (sueldo*12)/100;
        
        txtArea.append("El sueldo bruto es: " +sueldoBruto +"\n");
    }
    
