    String nombre;
    double sueldo;
    int año;
    private void btnProcesarActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnProcesarActionPerformed
        double bonificacion;
        nombre = txtSueldo.getText();
        sueldo = Double.parseDouble(txtSueldo.getText());
        año = Integer.parseInt(txtAño.getText());
        
        bonificacion = calcularBonificacion();
        
        mostrarResultado(bonificacion);
    }//GEN-LAST:event_btnProcesarActionPerformed
    public double calcularBonificacion(){
        double bonificacion;
        año = 2022 - año;
        if(año<=5){
            bonificacion = (sueldo*10)/100;
        }else if(año<=9){
            bonificacion = (sueldo * 16 )/100;
        }else{
            bonificacion = (sueldo*25)/100;
        }
        return bonificacion;
    }
    private void mostrarResultado(double bonificacion){
        txtArea.setText("La bonificacion para: " + nombre + " es de: "+ bonificacion);
    }
